import React, { useState } from 'react';
import styledComponents from 'styled-components';

import Button from '../../UI/Button/Button';
const FormControl = styledComponents`
  margin: 0.5rem 0;

& label {
  font-weight: bold;
  display: block;
  margin-bottom: 0.5rem;
  color: ${props=> (props.ivalid? 'red':'black')};
}

& input {
  display: block;
  width: 100%;
  border: 1px solid ${props => (props.invalid ? 'red':'black')};
  background : ${props => (props.invalid? 'rgb(152, 101, 101)':'transparent')};
  font: inherit;
  line-height: 1.5rem;
  padding: 0 0.25rem;
}

& input:focus {
  outline: none;
  background: #fad0ec;
  border-color: #8b005d;
}

`;
const CourseInput = props => {
  const [enteredValue, setEnteredValue] = useState('');
  const [isValid, setisValide] = useState(true);

  const goalInputChangeHandler = event => {
    setEnteredValue(event.target.value);
    if(enteredValue.trim().length > 0){
    setisValide(true);
    }
  };

  const formSubmitHandler = event => {
    event.preventDefault();
    if(enteredValue.trim().length === 0){
     setisValide(false);
     return;
    }
    props.onAddGoal(enteredValue);

  };

  return (
    <form onSubmit={formSubmitHandler}>
      <FormControl invalid = {!isValid}>
        <label >Course Goal</label>
        <input  type="text" onChange={goalInputChangeHandler} />
      </FormControl>
      <Button type="submit">Add Goal</Button>
    </form>
  );
};

export default CourseInput;
